import { useEffect } from 'react';

function UseBroom() {
	useEffect(() => {
		const script = document.getElementById('bugsOnTheLoose');
		if (script) {
			document.head.removeChild(script);
		}
		// We remove all the classes ".bug" from the body
		const bugs = document.querySelectorAll('.bug');
		bugs.forEach((bug) => {
			document.body.removeChild(bug);
		});
	}, []);

	return null;
}

export default UseBroom;
