/* eslint-disable no-undef */
import React, { useCallback, useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import useFetchJson from '@Hooks/useFetchJson';

import './ContactForm.css';

/**
 * @param {any} arg
 */
function ContactForm({ arg }) {
	const formPath = process.env.REACT_APP_API_URL;
	const formPathOption = arg;
	const [formIsFilled, setFormIsFilled] = useState(false);
	const [checkBoxDisabled, setCheckBoxDisabled] = useState(true);
	const [submitButtonDisabled, setSubmitButtonDisabled] = useState(true);
	const [checkBoxChecked, setCheckBoxChecked] = useState(false);
	const [inputs, setInputs] = useState([]);

	const [data, isError, loading, errorMessage] = useFetchJson(formPath, formPathOption);

	// We use a callback function to avoid infinite loop
	const settingInputs = useCallback(() => {
		setInputs([
			document.querySelector('#name'),
			document.querySelector('#firstname'),
			document.querySelector('#email'),
			document.querySelector('#phone'),
			document.querySelector('#message'),
		]);
	}, []);

	useEffect(() => {
		setInputs([
			document.querySelector('#name'),
			document.querySelector('#firstname'),
			document.querySelector('#email'),
			document.querySelector('#phone'),
			document.querySelector('#message'),
		]);

		const checkBox = document.querySelector('#rgpd1');

		const formCheck = inputs.every((input) => input !== null) ? inputs.every((input) => input.value !== '') : false;
		setFormIsFilled(formCheck);
		console.log('formCheck: ', formCheck);

		const checkForm = (form, checkbox) => {
			console.log('test: ', form);
			if (form) {
				setCheckBoxDisabled(false);
				if (checkbox === true) {
					setSubmitButtonDisabled(false);
				}
			} else {
				setCheckBoxDisabled(true);
				setSubmitButtonDisabled(true);
			}
		};

		const handleInputChange = () => {
			checkForm(formIsFilled, checkBoxChecked);
		};

		const handleCheckboxChange = () => {
			checkForm(formIsFilled, checkBoxChecked);
		};

		inputs.forEach((input) => {
			if (input !== null) {
				input.addEventListener('keyup', handleInputChange);
				input.addEventListener('change', handleInputChange);
				input.addEventListener('paste', handleInputChange);
				input.addEventListener('input', handleInputChange);
			}
		});

		checkBox !== null ? checkBox.addEventListener('change', handleCheckboxChange) : null;

		// To avoid memory leaks when component unmounts or re-renders
		return () => {
			inputs.forEach((input) => {
				if (input !== null) {
					input.removeEventListener('keyup', handleInputChange);
					input.removeEventListener('change', handleInputChange);
					input.removeEventListener('paste', handleInputChange);
					input.removeEventListener('input', handleInputChange);
				}
			});
			checkBox !== null ? checkBox.removeEventListener('change', handleCheckboxChange) : null;
		};
	}, [data, isError, loading, errorMessage, formIsFilled, checkBoxChecked, inputs]);

	return (
		<>
			{loading ? (
				<p>Loading...</p>
			) : isError ? (
				<p>{errorMessage}</p>
			) : (
				<form action='php/mail.php' method='POST'>
					{data.map((item, index) => (
						<div className='form__group' key={index}>
							{item.type === 'textarea' ? (
								<textarea
									name={item.name}
									id={item.id}
									placeholder={item.placeholder}
									cols={item.cols}
									rows={item.rows}
									required={item.required}
								></textarea>
							) : (
								<input
									type={item.type}
									name={item.name}
									id={item.id}
									placeholder={item.placeholder}
									required={item.required}
								/>
							)}
						</div>
					))}
					<div className='wrap__rgpd'>
						<div className='customCheckbox'>
							<input
								type='checkbox'
								value='
								J’accepte que les données saisies dans le formulaire soient utilisées pour me recontacter dans le cadre de ma demande'
								id='rgpd1'
								name='rgpd1'
								required={true}
								checked={checkBoxChecked}
								onChange={() => setCheckBoxChecked(checkBoxChecked === false ? true : false)}
								disabled={checkBoxDisabled}
							/>
							<label htmlFor='rgpd1'></label>
						</div>
						<p>
							En cochant cette case, j’accepte que les données saisies dans le formulaire ci-dessus soient
							utilisées par www.anti-nuisible87.fr pour me recontacter dans le cadre de ma demande. Les
							destinataires sont www.anti-nuisible87.fr et seulement ce dernier. Pour plus d'informations
							sur le traitement de vos données et l'exercice de vos droits, reportez-vous à notre{' '}
							<a className='legals__link' href='/mentions_legales'>
								politique de confidentialité
							</a>
							.
						</p>
					</div>
					<div className='form__group button'>
						<input
							className='submit__button'
							type='submit'
							value='Envoyer le formulaire'
							disabled={submitButtonDisabled}
						/>
					</div>
				</form>
			)}
		</>
	);
}

// arg prop is required and must be a string
ContactForm.propTypes = {
	arg: PropTypes.string.isRequired,
};

export default ContactForm;
