// A hook to fetch posts from a facebook page
// Usage: const { posts, loading, error } = useFacebook('pageId', 'accessToken', 5);

import { useState, useEffect } from 'react';

const fetchFacebook = (pageId, accessToken, limit) => {
	const [posts, setPosts] = useState([]);
	const [loading, setLoading] = useState(true);
	const [error, setError] = useState(false);

	const fetchPosts = async () => {
		try {
			const url = `https://graph.facebook.com/${pageId}/posts?access_token=${accessToken}&limit=${limit}`;
			const response = await fetch(url);
			const data = await response.json();
			setPosts(data.data);
			setLoading(false);
		} catch (err) {
			setError(true);
			setLoading(false);
		}
	};

	useEffect(() => {
		fetchPosts();
	}, []);

	return { posts, loading, error };
};

export default fetchFacebook;
