import React, { useEffect } from 'react';
import useFetch from '@Hooks/useFetch';
import Slides from '@Models/slides/slides';

import './Slideshow.css';

const Slideshow = () => {
	const slidesPath = 'http://localhost:8080/';
	const slidePathOption = 'slideShowCollection';

	const [data, isError, loading, errorMessage] = useFetch(slidesPath, slidePathOption);

	const [slides, setSlides] = React.useState(0);
	const delay = 2500;

	useEffect(() => {
		if (isError) {
			console.log(errorMessage);
		}

		if (loading) {
			console.log('Loading...');
		}

		if (data) {
			console.log(data);
		}

		setTimeout(() => setSlides((prevSlides) => (prevSlides === data.length - 1 ? 0 : prevSlides + 1)), delay);

		return () => {};
	}, [data, isError, loading, errorMessage, slides]);

	return (
		<section id='slideshow'>
			<h1>Slideshow</h1>
			{/* We check if it's loading then if there's an error and finally if it's ok, we display each picture (data[].path) with a slideshow */}
			{loading ? (
				<p>Loading...</p>
			) : isError ? (
				<p>{errorMessage}</p>
			) : (
				<div className='slideshow'>
					<div className='slideshowSlider' style={{ transform: `translate3d(${-slides * 100}%, 0, 0)` }}>
						{data.map((/** @type {{ id: any; path: string; alt: any; }} */ slide) => (
							<Slides
								key={slide.id}
								path={require('' + '@Images/' + slide.path)}
								alt={slide.alt}
								onClick={() => console.log('click')}
								isDelete={false}
								onDelete={() => console.log('delete')}
								isEdit={false}
								onEdit={() => console.log('edit')}
								isAdd={false}
								onAdd={() => console.log('add')}
							/>
						))}
					</div>
					<div className='slideshowDots'>
						{data.map((/** @type {any} */ slide, /** @type {React.Key} */ index) => (
							<div key={index} className='slideshowDot'></div>
						))}
					</div>
				</div>
			)}
		</section>
	);
};

export default Slideshow;
